`,(0,e.jsxDEV)(w,{name:"toast-destructive",children:[(0,e.jsxDEV)(n.div,{"data-rehype-pretty-code-fragment":"",children:(0,e.jsxDEV)(n.pre,{"data-language":"tsx","data-theme":"default",__rawString__:`"use client"

import { Button } from "@/components/ui/button"
import { ToastAction } from "@/components/ui/toast"
import { useToast } from "@/components/ui/use-toast"

export function ToastDestructive() {
  const { toast } = useToast()

  return (
    <Button
      variant="outline"
      onClick={() => {
        toast({
          variant: "destructive",
          title: "Uh oh! Something went wrong.",
          description: "There was a problem with your request.",
          action: <ToastAction altText="Try again">Try again</ToastAction>,
        })
      }}
    >
      Show Toast
    </Button>
  )
}
