e:"/workspace/dx-beta/frontend/www/ustudy/apps/www/content/docs/dark-mode/_mdx_bundler_entry_point-eab682ef-e360-4163-a1c3-7f388ad795ea.mdx"},this),"Add a mode toggle"]},void 0,!0,{fileName:"/workspace/dx-beta/frontend/www/ustudy/apps/www/content/docs/dark-mode/_mdx_bundler_entry_point-eab682ef-e360-4163-a1c3-7f388ad795ea.mdx",lineNumber:44,columnNumber:1},this),(0,n.jsxDEV)(e.div,{"data-rehype-pretty-code-fragment":"",children:[(0,n.jsxDEV)(e.div,{"data-rehype-pretty-code-title":"","data-language":"tsx","data-theme":"default",children:"src/components/ModeToggle.tsx"},void 0,!1,{fileName:"/workspace/dx-beta/frontend/www/ustudy/apps/www/content/docs/dark-mode/_mdx_bundler_entry_point-eab682ef-e360-4163-a1c3-7f388ad795ea.mdx"},this),(0,n.jsxDEV)(e.pre,{"data-language":"tsx","data-theme":"default",__withMeta__:!0,__rawString__:`import * as React from "react"
import { Moon, Sun } from "lucide-react"

import { Button } from "@/components/ui/button"
import {
  DropdownMenu,
  DropdownMenuContent,
  DropdownMenuItem,
  DropdownMenuTrigger,
} from "@/components/ui/dropdown-menu"

export function ModeToggle() {
  const [theme, setThemeState] = React.useState<
    "theme-light" | "dark" | "system"
  >("theme-light")

  React.useEffect(() => {
    const isDarkMode = document.documentElement.classList.contains("dark")
    setThemeState(isDarkMode ? "dark" : "theme-light")
  }, [])

  React.useEffect(() => {
    const isDark =
      theme === "dark" ||
      (theme === "system" &&
        window.matchMedia("(prefers-color-scheme: dark)").matches)
    document.documentElement.classList[isDark ? "add" : "remove"]("dark")
  }, [theme])

  return (
    <DropdownMenu>
      <DropdownMenuTrigger asChild>
        <Button variant="outline" size="icon">
